import React, { Component } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

export default class PostView extends Component {

    constructor(props) {
        super(props);

        this.onFileChange = this.onFileChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChange = this.onChange.bind(this);
        this.state = {
            imgCollection: '',
        }
    }

    onFileChange(e) {
        this.setState({ imgCollection: e.target.files })
    }

    onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
    };

    onSubmit(e) {
        e.preventDefault()

        var formData = new FormData();
        for (const key of Object.keys(this.state.imgCollection)) {
            formData.append('imgCollection', this.state.imgCollection[key])

        }
        formData.append('category', e.target['category'].value)
        axios.post("http://localhost:4000/upload-images", formData, {

        }).then(res => {
            console.log(res.data)
        })

      /*  axios.post("http://localhost:4000/upload-images", {
         title
        }).then(res => {
          console.log(res.data)
      }) */
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                <form method="get" action="" onSubmit={this.handleSubmit.bind(this)}>
                        <div > 
                        <h3>title</h3>
                        <input placeholder="Name"  type="text" name="title"></input>
                        </div>
                        <h3>description</h3>
                        <div > 
                        <input placeholder="Name"  type="text" name="description"></input>
                        </div>
                        <h3>category</h3>
                        <div > 
                        <input placeholder="Name"  type="text" name="category"></input>
                        </div>
                        <h3>location</h3>
                        <div > 
                        <input placeholder="Name"  type="text" name="location"></input>
                        </div>
                        <div className="container">
                                <h3>image</h3>
                                <div className="row">

                                        <div className="form-group">
                                            <input type="file" name="imgCollection" onChange={this.onFileChange.bind(this)} multiple />
                                        </div>
                                        <div className="form-group">
                                        </div>
                                </div>
                        </div>
                        <div>
                        <h3>price</h3>
                        <input placeholder="Name"  type="text" name="price"></input>
                        </div>
                        <div>
                        <h3>delivery</h3>
                        <select
                        id="searchSelect"
                        name="searchSelect"
                        onChange={this.selectedValueHandler.bind(this)} >
                        {this.state.options.map(item => (
                            <option key={item.value} value={item.value}>
                            {item.name}
                            </option>
                        ))}
                        </select>
                        </div>
                        <div>
                        <h3>seller of name</h3>
                        <input placeholder="Name"  type="text" name="SellerOfName"></input>
                        </div>
                        <div>
                            <h3>-------</h3>
                            <div><button type="submit" >Submit</button></div></div>
                </form>
                </div>
            </div>
        )
    }
}
