import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios';
import constants from '../constants.json';


export default class PostInfo extends Component {
  constructor(props)
  {
    super(props);
    this.state = {
      post: [],
      postFilter: "",
    };
  }  

  componentDidMount = () => {
    axios.get(constants.baseAddress + "/post")
      .then(res => {
        console.log(res.data);
        this.setState({ post: res.data });
      })
      .catch(error => console.log(error));
  }

  handleChange = (e) => {
    this.setState({postFilter: e.target.value.substr(0,20)});
  }

  render() {
    return (
      <div>
        <input type="search"  value={this.state.postFilter} onChange={this.handleChange.bind(this)} placeholder="Search..." />
        {this.state.post.filter(i => i.title.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1).map(
        i => 
          <div key={i.id}>
            <div>{i.image.map(url => <img src={url}></img>)}</div>
            <div>{i.title}</div>
            <div>{i.description}</div>
            <div>{i.category}</div>
            <div>{i.location}</div>
            <div>{i.price}</div>
            <div>{i.dataOfPosting}</div>
            <div>{i.delivery}</div>
            <div>{i.SellerOfName}</div>
          </div>

        )}
      </div>
    )
  }
}
