import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios';
import constants from '../constants.json';


export default class PostInfo extends Component {
  constructor(props)
  {
    super(props);
    this.state = {
      post: [],
      search: "",
      selectedValue: 'title',
      options: [
        {
          name: 'Title',
          value: 'tile',
        },
        {
          name: 'Location',
          value: 'location',
        },
        {
          name: 'Price',
          value: 'price',
        }
      ]
    };
  }  

  componentDidMount = () => {
    axios.get(constants.baseAddress + "/post")
      .then(res => {
        console.log(res.data);
        this.setState({ post: res.data });
      })
      .catch(error => console.log(error));
  }

  handleChange = (e) => {
    this.setState({search: e.target.value});
  }

  selectedValueHandler (e) {
    this.setState({selectedValue: e.target.value});
    }

  render() {
        var FilterPost ;
        if (this.state.selectedValue === 'title') {
        FilterPost = this.state.post.filter(i => i.title.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1)
        }else if (this.state.selectedValue === 'location') {
        FilterPost = this.state.post.filter(i => i.location.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1)
        } else if (this.state.selectedValue === 'price') {
        FilterPost = this.state.post.filter(i => i.price.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1)
        }
    return (
      <div>
        <div>
        <select
        id="searchSelect"
        name="searchSelect"
        onChange={this.selectedValueHandler.bind(this)} >
        {this.state.options.map(item => (
          <option key={item.value} value={item.value}>
            {item.name}
          </option>
        ))}
        </select>
        <input type='text'
        value={this.state.search}
        onChange={this.handleChange.bind(this)} />
        </div>
        <div>
        {FilterPost.map(i => 
            <div key={i.id} style={{width: "300px"}}>
               <div>{i.image.map(url => <div key={url}><img style={{float: "left", width: "8%"}} src={url}></img></div>)}</div>
               <div>{i.title}</div>
               <div>{i.description}</div>
               <div>{i.category}</div>
               <div>{i.location}</div>
               <div>{i.price}</div>
               <div>{i.dataOfPosting}</div>
               <div>{i.delivery}</div>
               <div>{i.SellerOfName}</div>
            </div>
        )}
        </div>
      </div>
    )
  }
}
