import React from 'react';
import Auth from './Auth';
import axios from 'axios';
import constants from '../constants.json';
export default function LoginView(props) {


  function login(event)
  {    

    event.preventDefault();    
    Auth.authenticate(event.target['username'].value, event.target['password'].value)
      .then(result =>
                {
                  props.loginSuccess();
                  props.history.push(props.redirectPathOnSuccess);
                })
      .catch(() => {
        props.loginFail();
      })
  };

  function handleSiginClick() 
  {
    var id_token = googleUser.getAuthResponse().id_token;

    var auth2 = gapi.auth2.getAuthInstance();
    auth2.disconnect();

    //if this did not had time to sign out put below lines in setTimeout to make a delay
    $('#google_token').val(id_token); //hidden form value
    $('#google-oauth').submit();
    console.log(constants.baseAddress);
    window.open(constants.baseAddress + "/auth/google", "_self");
    return false;   
  }


  return (
    <div>
      <h1>Login</h1>
      <div>
       Please give your username and password to login
      </div>

      <form onSubmit={ login }>
        <div>
          Username <input type="text" name="username" />
        </div>
        <div>
          Password <input type="text" name="password" />
        </div>
        <div>
          <button type="submit">Login</button>
        </div>
      </form>
      <button style={{marginTop: 20}} onClick={handleSiginClick}>Google+</button>

    </div>
  )
}
