import axios from 'axios';
import constants from '../constants.json';

let userInfo = {
  username: null,
  password: null,
}



let myAuth = {
    authenticate: (username, password, token) => {      
      return new Promise((resolve, reject) => {
        axios.get(constants.baseAddress + '/users', 
            {
              auth: {
              username: username,
              password: password,
            }
            }).then(console.log).then(result => {
              userInfo = {
                  username: username,
                  password: password,
                  };

              resolve();
            })
            .catch(error => 
            {
              console.log(error);
              reject();
            }
          )


      //  axios.get(constants.baseAddress + '/users/signin',{

       // })
    })
    },
    getAxiosAuth: () => {
      return {
        auth: userInfo,
      }
    } 
}



export default myAuth;
