                "price": "400",
                "dataOfPosting": "1-2-2020",
                "delivery" : "Shipping",
                "SellerOfName": "Anna - 01241325"
            },
            {
                "id" : 2,
                "title" : "Clothing for new",
                "description": "6 months",
                "category": "Clothing",
                "location": "Helsinki",
                "image": [
                    "http://localhost:4000/public/d77fc158-c00a-4ad0-a0fb-9be4e8d4898f-download-(1).jpeg",
                    "http://localhost:4000/public/cf791e69-4fa2-4b86-b359-2a3aa8b0ac10-download-(2).jpeg",
                    "http://localhost:4000/public/5c766aa8-92b4-4e93-ab39-ad89fdd85c57-download.jpeg"
                ],
                "price": "200",
                "dataOfPosting": "2-2-2020",
                "delivery" : "Pickup",
                "SellerOfName": "Henkoi - 012413251"
            }]
        };
    }



  render() {
      let  a = this.state.posts.map(
        i => {(
          i.image.map(url => <div>{<img src={`http://localhost:4000/public${url.image}`}></img>}</div>)
          )}
    )
    return (
      <div>
        {a}
      </div>
    )
  }
}
