import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import LoginView from './components/LoginView';
import Auth from './components/Auth';
import axios from 'axios';
import constants from './constants.json';
import Home from './Home';


export default class App extends Component {
  constructor(props)
  {
    super(props);
    this.state = {
      isAuthenticated: false,
      someData: null,
      message: "",
      data:""
    };
  }  

  componentDidMount = () =>
   {
     axios.get(constants.baseAddress + '/users/:id').then(result => {
       this.setState({ item: result.token });
     })
     .catch(error => {
       console.error(error);
     })
   }





  onLogin = () => {
    this.setState({ isAuthenticated: true })
  }

  onLoginFail = () => {
    this.setState({ isAuthenticated: false });
    console.log("Login failed");
  }

  /* This function illustrates how some protected API could be accessed */


  render() {

    return (
      <div>
      <Router>
        <Route path="/" exact render={
          (routeProps) =>
            <LoginView
              loginSuccess = { this.onLogin }
              loginFail = { this.onLoginFail }
              data = {this.state.data}
              userInfo={ this.state.userInfo }
              redirectPathOnSuccess="/example"
              {...routeProps}
              />
        } />
        <Route path="/example" component={Home} />
      </Router>
      </div>
    )
  }
}
