import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios';
import constants from '../constants.json';


export default class PostInfo extends Component {
  constructor(props)
  {
    super(props);
    this.state = {
      post: [],
      search: "",
      roleFilter: null,
      isLoading: false,
    };
  }  

  componentDidMount = () => {
    axios.get(constants.baseAddress + "/post")
      .then(res => {
        console.log(res.data);
        this.setState({ post: res.data });
      })
      .catch(error => console.log(error));
  }

  handleChange = (e) => {
    this.setState({search: e.target.value.trim().toLowerCase()});
  }

  changedRole = e => {
    this.setState({ roleFilter: e.target.value })
  }

  render() {
        if (this.state.selectedValue === 'title') {
        let filteredBooks = this.state.post.filter(book => {
          return book.name.toLowerCase().indexOf(this.state.search) !== -1;
        })
        } else if (this.state.selectedValue === 'author') {
        let filteredBooks = this.state.post.filter(book => {
          return book.author.toLowerCase().indexOf(this.state.search) !== 
        -1;
        })
        } else if (this.state.selectedValue === 'isbn') {
        let filteredBooks = this.state.post.filter(book => {
          return book.isbn.indexOf(this.state.search) !== -1;
        })
        }
    return (
      <div>
        <select
        onChange={this.changedRole.bind(this)} >
        {this.state.post.map(item => (
          <option key={item.id} value={item.title}>
            {item.title}
          </option>
        ))}
        </select>
        {filteredBooks.map(i => {
            <div>
                {i.title}
            </div>
        })}
      </div>
    )
  }
}
