import React, { Component } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

export default class PostView extends Component {

    constructor(props) {
        super(props);

        this.onFileChange = this.onFileChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.onChange = this.onChange.bind(this);
        this.state = {
            imgCollection: '',
            category: '', 

        }
    }

    onFileChange(e) {
        this.setState({ imgCollection: e.target.files })
    }

    onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
    };

    onSubmit(e) {
        e.preventDefault()

        var formData = new FormData();
        for (const key of Object.keys(this.state.imgCollection)) {
            formData.append('imgCollection', this.state.imgCollection[key])

        }
        formData.append('category', e.target['category'])
        const {title} = this.state
        axios.post("http://localhost:4000/upload-images", formData, {

        }).then(res => {
            console.log(res.data)
        })

      /*  axios.post("http://localhost:4000/upload-images", {
         title
        }).then(res => {
          console.log(res.data)
      }) */
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <form onSubmit={this.onSubmit}>
                        <div className="form-group">
                          <div><input placeholder="Name"  type="text" name="category"  onChange={this.onChange}></input></div>
                            <input type="file" name="imgCollection" onChange={this.onFileChange} multiple />
                        </div>
                        <div className="form-group">
                            <button className="btn btn-primary" type="submit">Upload</button>
                            <button><Link to='/mainpage'>Home</Link></button>
                        </div>
                    </form>
                </div>
            </div>
        )
    }
}
