import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios';
import constants from '../constants.json';
import Auth from './Auth';
import PropTypes from "prop-types";

export default class PostInfo extends Component {

  static propTypes = {
    authenticated: PropTypes.bool.isRequired
  };

  constructor(props)
  {
    super(props);
    this.state = {
      user: {},
      error: null,
      authenticated: false,
      post: [],
      search: '',
      selectedValue: 'category',
      options: [
        {
          name: 'Category',
          value: 'category',
        },
        {
          name: 'Location',
          value: 'location',
        },
        {
          name: 'DateOfPosting',
          value: 'dateOfPosting',
        }
      ]
    };
  }  

  componentDidMount = () => {
    axios.get(constants.baseAddress + "/post")
      .then(res => {
        console.log(res.data);
        this.setState({ post: res.data });
      })
      .catch(error => console.log(error));
  }

  handleChange = (e) => {
    this.setState({search: e.target.value.substr(0,20)});
  }

  selectedValueHandler = (e) => {
    this.setState({selectedValue: e.target.value});
    }





  handleOnDelete = (id) =>{
      const { authenticated } = this.props;
      if(authenticated == true){
        axios.delete("http://localhost:4000/post/" + id
        ).then(function (response) {
            console.log(response);
        })
            .catch(function (error) {
                console.log(error);
            });
        let deleted_array_of_post = this.state.post.filter(i => i.id != id);
        console.log(deleted_array_of_post);
        this.setState({post : deleted_array_of_post});  
      }

    }

    componentDidMount() {
      // Fetch does not send cookies. So you should add credentials: 'include'
      fetch("http://localhost:4000/auth/login/success", {
        method: "GET",
        credentials: "include",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
          "Access-Control-Allow-Credentials": true
        }
      })
        .then(response => {
          if (response.status === 200) return response.json();
          throw new Error("failed to authenticate user");
        })
        .then(responseJson => {
          this.setState({
            authenticated: true,
            user: responseJson.user
          });
        })
        .catch(error => {
          this.setState({
            authenticated: false,
            error: "Failed to authenticate user"
          });
        });
    }
    _handleSignInClick = () => {
      // Authenticate using via passport api in the backend
      // Open Twitter login page
      // Upon successful login, a cookie session will be stored in the client
      window.open("http://localhost:4000/auth/google", "_self");
    };

    _handleLogoutClick = () => {
      // Logout using Twitter passport api
      // Set authenticated state to false in the HomePage
      window.open("http://localhost:4000/auth/logout", "_self");
      this.setState({ authenticated: false });
    };


  render() {
        const { authenticated } = this.state;
        let FilterPost;
        if(this.state.selectedValue === 'category'){
          FilterPost = this.state.post.filter(i => i.category.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1)  
        }else if (this.state.selectedValue === 'location') {
        FilterPost = this.state.post.filter(i => i.location.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1)
        } else if (this.state.selectedValue === 'dateOfPosting') {
        FilterPost = this.state.post.filter(i => i.dataOfPosting.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1)
        }
    return (
      <div>
          <ul className="menu">
        <li>
          <Link to="/">Home</Link>
        </li>
        {authenticated ? (
          <li onClick={this._handleLogoutClick}>Logout</li>
        ) : (
          <li onClick={this._handleSignInClick}>Login</li>
        )}
        </ul>
        <div>
          {!authenticated ? (
            <h1>Welcome!</h1>
          ) : (
            <div>
              <h1>You have login succcessfully!</h1>
            </div>
          )}
        </div>
        <button><Link to={'/login'}>Log in</Link></button>
        <div>
        <select
        id="searchSelect"
        name="searchSelect"
        onChange={this.selectedValueHandler.bind(this)} >
        {this.state.options.map(item => (
          <option key={item.value} value={item.value}>
            {item.name}
          </option>
        ))}
        </select>
        <input type='text'
        value={this.state.search}
        onChange={this.handleChange.bind(this)} />
        </div>
        <div>
        {FilterPost.map(i => 
            <div key={i.id}>
               <div>{i.image.map(url => <img src={url} style={{width: "8%"}}></img>)}</div>
               <div>{i.title}</div>
               <div>{i.description}</div>
               <div>{i.category}</div>
               <div>{i.location}</div>
               <div>{i.price}</div>
               <div>{i.dataOfPosting}</div>
               <div>{i.delivery}</div>
               <div>{i.SellerOfName}</div>
               <div>
                 <button>Change</button>
                 <button onClick={this.handleOnDelete.bind(this, i.id)}>Delete</button>
               </div>
            </div>
        )}
        </div>
      </div>
    )
  }
}
