import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios';
import constants from '../constants.json';
import Auth from './Auth';
import PropTypes from 'prop-types';


export default class PostInfo extends Component {

  constructor(props)
  {
    super(props);
    this.state = {
      post: [],
      search: '',
      selectedValue: 'category',
      options: [
        {
          name: 'Category',
          value: 'category',
        },
        {
          name: 'Location',
          value: 'location',
        },
        {
          name: 'DateOfPosting',
          value: 'dateOfPosting',
        }
      ]
    };
  }  




  componentDidMount = () => {
    axios.get(constants.baseAddress + "/post")
      .then(res => {
        console.log(res.data);
        this.setState({ post: res.data });
      })
      .catch(error => console.log(error));
  }

  handleChange = (e) => {
    this.setState({search: e.target.value.substr(0,20)});
  }

  selectedValueHandler = (e) => {
    this.setState({selectedValue: e.target.value});
    }

  responseGoogle = (response) => {
      console.log(response);
    }

  handleOnLogOut = (e) =>{
    e.preventDefault();
    axios.get(constants.baseAddress + "/auth/logout")
  }


  handleOnDelete = (id) =>{
    axios.delete("http://localhost:4000/post/" + id
    ).then(function (response) {
        console.log(response);
    })
        .catch(function (error) {
            console.log(error);
        });
    let deleted_array_of_post = this.state.post.filter(i => i.id != id);
    console.log(deleted_array_of_post);
    this.setState({post : deleted_array_of_post});   
  }

  handleSiginClick() 
  {
    window.open(constants.baseAddress + "/auth/google", "_self");  
  }



  render() {
        var FilterPost = this.state.post;
        if (this.state.selectedValue === 'category') {
        FilterPost = FilterPost.filter(i => i.category.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1).map(i => 
          <div key={i.id}>
             <div>{i.image.map(url => <img src={url} style={{width: "8%"}}></img>)}</div>
             <div>{i.title}</div>
             <div>{i.description}</div>
             <div>{i.category}</div>
             <div>{i.location}</div>
             <div>{i.price}</div>
             <div>{i.dataOfPosting}</div>
             <div>{i.delivery}</div>
             <div>{i.SellerOfName}</div>
             <div>
               <button>Change</button>
               <button onClick={this.handleOnDelete.bind(this, i.id)}>Delete</button>
             </div>
          </div>
      )
        }else if (this.state.selectedValue === 'location') {
        FilterPost = FilterPost.filter(i => i.location.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1).map(i => 
          <div key={i.id}>
             <div>{i.image.map(url => <img src={url} style={{width: "8%"}}></img>)}</div>
             <div>{i.title}</div>
             <div>{i.description}</div>
             <div>{i.category}</div>
             <div>{i.location}</div>
             <div>{i.price}</div>
             <div>{i.dataOfPosting}</div>
             <div>{i.delivery}</div>
             <div>{i.SellerOfName}</div>
             <div>
               <button>Change</button>
               <button onClick={this.handleOnDelete.bind(this, i.id)}>Delete</button>
             </div>
          </div>
      )
        } else if (this.state.selectedValue === 'dateOfPosting') {
        FilterPost = FilterPost.filter(i => i.dataOfPosting.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1).map(i => 
          <div key={i.id}>
             <div>{i.image.map(url => <img src={url} style={{width: "8%"}}></img>)}</div>
             <div>{i.title}</div>
             <div>{i.description}</div>
             <div>{i.category}</div>
             <div>{i.location}</div>
             <div>{i.price}</div>
             <div>{i.dataOfPosting}</div>
             <div>{i.delivery}</div>
             <div>{i.SellerOfName}</div>
             <div>
               <button>Change</button>
               <button onClick={this.handleOnDelete.bind(this, i.id)}>Delete</button>
             </div>
          </div>
      )
        }
    return (
      <div>
        <button style={{marginTop: 20}} onClick={this.handleSiginClick.bind(this)}>Google+</button>


        <button><Link to={'/login'}>Log in</Link></button>

        <div>
        <select
        id="searchSelect"
        name="searchSelect"
        onChange={this.selectedValueHandler.bind(this)} >
        {this.state.options.map(item => (
          <option key={item.value} value={item.value}>
            {item.name}
          </option>
        ))}
        </select>
        <input type='text'
        value={this.state.search}
        onChange={this.handleChange.bind(this)} />
        </div>
        <div>
        {FilterPost}
        </div>
      </div>
    )
  }
}
