import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import axios from 'axios';
import constants from '../constants.json';


export default class PostInfo extends Component {
  constructor(props)
  {
    super(props);
    this.state = {
      post: [],
      postFilter: "",
      roleFilter: null,
      isLoading: false,
    };
  }  

  componentDidMount = () => {
    axios.get(constants.baseAddress + "/post")
      .then(res => {
        console.log(res.data);
        this.setState({ post: res.data });
      })
      .catch(error => console.log(error));
  }

  handleChange = (e) => {
    this.setState({postFilter: e.target.value.trim().toLowerCase()});
  }

  changedRole = e => {
    this.setState({ roleFilter: e.target.value })
  }

  render() {
    let text = this.state.post;
    if (this.state.postFilter) {
        text = text.filter(info => info.role.toLowerCase().match(this.state.postFilter));
    }
    return (
      <div>
        <select onChange={this.changedRole.bind(this)}>
                        <option value={''}></option>
                        {text.map(info => (
                            <div>
                                <h1>category</h1>
                                <option value={info.category}>{info.category}</option>
                            </div>

                        ))}
                    </select>

        {text.filter(info => this.state.postFilter ? info.role.includes(this.state.postFilter) : true).map(i => (
                        <div>
                            <div>{i.image.map(url => <img src={url}></img>)}</div>
                            <div>{i.title}</div>
                            <div>{i.description}</div>
                            <div>{i.category}</div>
                            <div>{i.location}</div>
                            <div>{i.price}</div>
                            <div>{i.dataOfPosting}</div>
                            <div>{i.delivery}</div>
                            <div>{i.SellerOfName}</div>
                        </div>
                    ))}
      </div>
    )
  }
}
