import React from 'react';
import Auth from './Auth';
import axios from 'axios';
import constants from '../constants.json';
export default function LoginView(props) {


  function login(event)
  {    
    event.preventDefault();    
    Auth.authenticate(event.target['username'].value, event.target['password'].value)
      .then(result =>
                {
                  props.loginSuccess();
                  props.history.push(props.redirectPathOnSuccess);
                })
      .catch(() => {
        props.loginFail();
      })
  };

  function handleSiginClick(e) 
  {

    axios.get(constants.baseAddress + "/auth/google").then((e)=>{
      const token = e.data.token;
      const config = {
          headers: { Authorization: `Bearer ${token}` }
      };
      axios.get(constants.baseAddress + '/auth/google/redirect',
          config
      )})
  }

  return (
    <div>
      <h1>Login</h1>
      <div>
       Please give your username and password to login
      </div>

      <form onSubmit={ login }>
        <div>
          Username <input type="text" name="username" />
        </div>
        <div>
          Password <input type="text" name="password" />
        </div>
        <div>
          <button type="submit">Login</button>
        </div>
      </form>
      <a href="http://localhost:4000/auth/google" class="button">Gmail+</a>
      <button style={{marginTop: 20}} onClick={handleSiginClick}>Google+</button>

    </div>
  )
}
