import React, { Component } from 'react';
import axios from 'axios';
import constants from '../constants.json';
import { Redirect, Route } from "react-router-dom";

class PostView extends React.Component {
  constructor(props) {
		super(props);

		this.state = {
      imgCollection: '',
      post: [],
		};

	}

  componentDidMount(){
    axios.get(constants.baseAddress + "/post")
    .then(res => {
      console.log(res.data);
      this.setState({ post: res.data });
    })
    .catch(error => console.log(error));
  }

  onFileChange(e) {
    this.setState({ imgCollection: e.target.files })
  }

  handleSubmit = e => {
    e.preventDefault()

        var formData = new FormData();
        for (const key of Object.keys(this.state.imgCollection)) {
            formData.append('imgCollection', this.state.imgCollection[key])
        }

        e.preventDefault();
        axios.post('http://localhost:4000/post/', {
          id: this.state.post.posts.length + 1,
          title: e.target['name'].value,
          image: formData,
          name : e.target['name'].value,
          ingredients : e.target['ingredients'].value,
          recipe : e.target['recipe'].value,
        })
  }
  render() {

    return(
      <div>
      <div>
        <form method="get" action="" onSubmit={this.handleSubmit}>
          <div > 
          <h3>title</h3>
          <input placeholder="Name"  type="text" name="title"></input>
          </div>
          <h3>description</h3>
          <div > 
          <input placeholder="Name"  type="text" name="description"></input>
          </div>
          <h3>category</h3>
          <div > 
          <input placeholder="Name"  type="text" name="category"></input>
          </div>
          <h3>location</h3>
          <div > 
          <input placeholder="Name"  type="text" name="location"></input>
          </div>
          <div className="container">
                <h3>image</h3>
                <div className="row">

                        <div className="form-group">
                            <input type="file" name="imgCollection" onChange={this.onFileChange} multiple />
                        </div>
                        <div className="form-group">
                        </div>
                </div>
          </div>
          <div>
          <h3>price</h3>
          <input placeholder="Name"  type="text" name="price"></input>
          </div>
          <div>
          <h3>delivery</h3>
          <select>
            <option name="delivery">delivery</option>
            <option name="pickup">pickup</option>
          </select>
          </div>
          <div>
          <h3>seller of name</h3>
          <input placeholder="Name"  type="text" name="SellerOfName"></input>
          </div>
          <div>
            <h3>-------</h3>
            <div><button type="submit" >Submit</button></div></div>
        </form>
      </div>
      </div>
    )

    }
}

export default PostView
