import axios from 'axios';
import constants from '../constants.json';

let userInfo = {
  username: null,
  password: null,
}



let myAuth = {
    authenticate: (username, password, token) => {      
      return new Promise((resolve, reject) => {
        axios.get(constants.baseAddress + '/users/:id', 
          {
              auth: {
              username: username,
              password: password
            }
          }).then( (f) => {
            const token = f.data.token;
            if (token) {
                axios.defaults.headers.post['/users/signin'] = `Bearer ${ token }`
               } else {
                delete axios.defaults.headers.post['/users/signin']
            }
            console.log(token);
          })
          .then(response => response.data.token)
          .then(result => {
                userInfo = {
                    username: username,
                    password: password,
                    };
            resolve();
          })
          .catch(error => 
            {
              console.log(error);
              reject();
            }
          )
    })
    },
    getAxiosAuth: () => {
      return {
        auth: userInfo
      }
    } 
}



export default myAuth;
