import React, { Component } from 'react';
import axios from 'axios';
import constants from '../constants.json';
import { Redirect, Route } from "react-router-dom";

class PostView extends React.Component {
  constructor(props) {
		super(props);

		this.state = {
      imgCollection: '',
      post: [],
      selectedValue: 'delivery',
      options: [
        {
          name: 'delivery',
          value: 'delivery',
        },
        {
          name: 'pickup',
          value: 'pickup',
        }
      ]
		};

	}

  selectedValueHandler = (e) => {
    this.setState({selectedValue: e.target.value});
    }

  componentDidMount(){
    axios.get(constants.baseAddress + "/post")
    .then(res => {
      console.log(res.data);
      this.setState({ post: res.data });
    })
    .catch(error => console.log(error));
  }

  onFileChange(e) {
    this.setState({ imgCollection: e.target.files })
  }

  handleSubmit = e => {

    // form is valid! We can parse and submit data

    e.preventDefault()

        var formData = new FormData();
        for (const key of Object.keys(this.state.imgCollection)) {
            formData.append('imgCollection', this.state.imgCollection[key])
        }

        e.preventDefault();
        axios.post(constants.baseAddress + "/post", {
          title: e.target['title'].value,

        })
        if (!e.target.checkValidity()) {
          // form is invalid! so we do nothing
          console.log("sss")
          return;
        }
        console.log(e.target['title'].value);
  }
  render() {
    console.log(this.state.selectedValue)
    return(
      <div>
      <div>
        <form method="get" action="" onSubmit={this.handleSubmit.bind(this)}>
          <div > 
          <h3>title</h3>
          <input placeholder="Name"  type="text" name="title"></input>
          </div>
          <h3>description</h3>
          <div > 
          <input placeholder="Name"  type="text" name="description"></input>
          </div>
          <h3>category</h3>
          <div > 
          <input placeholder="Name"  type="text" name="category"></input>
          </div>
          <h3>location</h3>
          <div > 
          <input placeholder="Name"  type="text" name="location"></input>
          </div>
          <div className="container">
                <h3>image</h3>
                <div className="row">

                        <div className="form-group">
                            <input type="file" name="imgCollection" onChange={this.onFileChange.bind(this)} multiple />
                        </div>
                        <div className="form-group">
                        </div>
                </div>
          </div>
          <div>
          <h3>price</h3>
          <input placeholder="Name"  type="text" name="price"></input>
          </div>
          <div>
          <h3>delivery</h3>
          <select
          id="searchSelect"
          name="searchSelect"
          onChange={this.selectedValueHandler.bind(this)} >
          {this.state.options.map(item => (
            <option key={item.value} value={item.value}>
              {item.name}
            </option>
          ))}
          </select>
          </div>
          <div>
          <h3>seller of name</h3>
          <input placeholder="Name"  type="text" name="SellerOfName"></input>
          </div>
          <div>
            <h3>-------</h3>
            <div><button type="submit" >Submit</button></div></div>
        </form>
      </div>
      </div>
    )

    }
}

export default PostView
