let userInfo = {
  username: null,
  password: null
}

let myAuth = {
    authenticate: (username, password) => {
      return new Promise((resolve, reject) => {
        axios.get('http://localhost:4000' + '/users/:id'+ username,
          {
              auth: {
              username: username,
              password: password
            }
          })
          .then(result => {
            userInfo = {
              username: username,
              password: password,
            }
            console.log(userInfo)
            resolve(result);
          })
          .catch(error =>
            {
              console.log(error);
              reject();
            }
          )
      });
    },
    getAxiosAuth: () => {
      return {
        auth: userInfo
      }
    }
}

export default myAuth;
