import axios from 'axios';
import constants from '../constants.json';

let userInfo = {
  username: null,
  password: null,
}




let myAuth = {
    authenticate: (username, password, token) => {      
      return new Promise((resolve, reject) => {
        axios.get(constants.baseAddress + '/users/:id', 
          {
              auth: {
              username: username,
              password: password,

            }


          }).then((e)=>{
            axios.get(constants.baseAddress + '/users/signin',
            {
                auth: {
                jwt_payload: e.data.token
                }
            }
            )
          })
          .then(result => {
                userInfo = {
                    username: username,
                    password: password,

                    };
          }).then(result => {
            jwt_payload = token
          })
          .catch(error => 
            {
              console.log(error);
              reject();
            }
          )


      //  axios.get(constants.baseAddress + '/users/signin',{

       // })
    })
    },
    getAxiosAuth: () => {
      return {
        auth: userInfo,
      }
    } 
}



export default myAuth;
