import React, { Component } from 'react'
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import LoginView from './components/LoginView';
import Auth from './components/Auth';
import axios from 'axios';
import constants from './constants.json';
import PostInfo from './components/PostInfo';
import PostView from './components/PostView';


export default class App extends Component {
  constructor(props)
  {
    super(props);
    this.state = {
      isAuthenticated: false,
      someData: null,
      message: "",
      userInfo: null,
    };
  }  

  onLogin = () => {
    this.setState({ isAuthenticated: true })
    this.setState({userInfo : responseJson.user })
  }

  onLoginFail = () => {
    this.setState({ isAuthenticated: false });
    console.log("Login failed");
  }

  /* This function illustrates how some protected API could be accessed */


  render() {
    console.log(this.state.userInfo);
    console.log(this.state.isAuthenticated)
    return (
      <div>
      <Router>

        <Route path="/login" exact render={
          (routeProps) =>
            <LoginView
              isAuthenticated={this.state.isAuthenticated}
              loginSuccess = { this.onLogin }
              loginFail = { this.onLoginFail }
              redirectPathOnSuccess="/"
              {...routeProps}
            />
        } />
        <Route path="/" exact render={
          (routeProps) => <PostInfo isAuthenticated={this.state.isAuthenticated} loginSuccess = { this.onLogin } {...routeProps} />
        }/>
        <Route path="/postview" exact render={
          (routeProps) => <PostView {...routeProps} />
        }/>

      </Router>
      </div>
    )
  }
}
