import React, { Component } from 'react';
import axios from 'axios';

import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import constants from '../constants.json';

export default class PostDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {
          post:[],
          hasError: false
        };
      }
      static getDerivedStateFromError(error) {
        axios.get(constants.baseAddress + "/view/" + parseInt(this.props.match.params.id))
            .then(res => {
            console.log(res.data);
            this.setState({ post: res.data});
            })
        return { hasError: true };
      }

      componentDidMount() {
        if(this.props.user !== ''){
            axios.get(constants.baseAddress + "/view/" + parseInt(this.props.match.params.id))
            .then(res => {
            console.log(res.data);
            this.setState({ post: res.data});
        })
        }
      };

    render(){
        console.log(parseInt(this.props.match.params.id));
        console.log(this.state.post);
       // const productData = this.props.getProductInfo(parseInt(this.props.match.params.id));
       if(this.state.post){
        return (
            <div>
                <div key={this.state.post.id} style={{paddingTop: 20}}>
                   <div>{this.state.post.image.map(url => <img src={url} style={{width: "8%"}}></img>)}</div>
                   <div>{this.state.post.title}</div>
                   <div>{this.state.post.description}</div>
                   <div>{this.state.post.category}</div>
                   <div>{this.state.post.location}</div>
                   <div>{this.state.post.price}</div>
                   <div>{this.state.post.dataOfPosting}</div>
                   <div>{this.state.post.delivery}</div>
                   <div>{this.state.post.SellerOfName}</div>
                   <button><Link to={`/postdetail/${this.state.post.id}`}>Change</Link></button>
                </div>
            )}
            </div>

        )
       }

    }
}
